using InvoiceWebApp.Components.Entities;
using InvoiceWebApp.Components.Helpers;
using InvoiceWebApp.Components.Services.Interfaces;
using InvoiceWebApp.Controllers.ViewModels;

using Microsoft.AspNetCore.Cors;
using Microsoft.AspNetCore.Mvc;
using Microsoft.Extensions.Primitives;

using Newtonsoft.Json;

using System;
using System.Collections.Generic;
using System.IO;
using System.Linq;
using System.Text.RegularExpressions;
using System.Threading.Tasks;

namespace InvoiceWebApp.Controllers {
	[EnableCors("AllowAll")]
    [Produces("application/json")]
    [Route("api/users")]
    public class UsersController : Controller
    {
		private readonly IUserRepository _repo;
		private Email _email;

        public UsersController(IUserRepository repo, Email email)
        {
			this._repo = repo;
			this._email = email;
        }

        /// <summary>
        /// User pagination.
        /// </summary>
        /// <param name="page">Page</param>
        /// <param name="pageSize">Amount of items on one page</param>
        [HttpGet("index")]
        [ProducesResponseType(typeof(PaginationResult<UserViewModel>), 200)]
        [ProducesResponseType(typeof(void), 500)]
        public async Task<IActionResult> Index(int? page, int? pageSize)
        {
            if (!page.HasValue || !pageSize.HasValue)
            {
                return StatusCode(400, String.Format("Invalid parameter(s)."));
            }

            //Get data
            var data = await _repo.GetUsers();
            if (data == null)
            {
                return StatusCode(500, "Users could not be found.");
            }

            //Convert to viewmodel
            var result = data.Select(s => new UserViewModel
            {
                Email = s.Email,
                Password = s.Password,
                FirstName = s.FirstName,
                LastName = s.LastName,
                CompanyName = s.CompanyName,
                Role = s.Role,
                Picture = s.Picture,
                NameLabel = s.CompanyName == null ? s.FirstName + " " + s.LastName : s.CompanyName
            });

            var totalPages = ((result.Count() - 1) / pageSize.Value) + 1;
            var requestedData = result.Skip((page.Value - 1) * pageSize.Value).Take(pageSize.Value).ToList();

            var paging = new PaginationResult<UserViewModel>(page.Value, totalPages, requestedData);
            var pagingResult = new PaginationResultViewModel<UserViewModel>
            {
                Data = paging.Data,
                TotalPages = paging.TotalPages,
                CurrentPage = paging.CurrentPage
            };

            return Ok(pagingResult);
        }

        /// <summary>
        /// Gets a list with all users.
        /// </summary>
        [HttpGet("getAll")]
        [ProducesResponseType(typeof(IEnumerable<UserViewModel>), 200)]
        [ProducesResponseType(typeof(void), 500)]
        public async Task<IActionResult> GetAll()
        {
            //Get data
            var data = await _repo.GetUsers();
            if (data == null)
            {
                return StatusCode(500, "Users could not be found.");
            }

            //Convert to viewmodel
            var result = data.Select(s => new UserViewModel
            {
                Email = s.Email,
                Password = s.Password,
                FirstName = s.FirstName,
                LastName = s.LastName,
                CompanyName = s.CompanyName,
                Role = s.Role,
                Picture = s.Picture,
                NameLabel = s.CompanyName == null ? s.FirstName + " " + s.LastName : s.CompanyName
            });

            return Ok(result);
        }

        /// <summary>
        /// Gets a user by email.
        /// </summary>
        /// <param name="email">Email of user</param>
        [HttpGet("getByEmail")]
        [ProducesResponseType(typeof(UserViewModel), 200)]
        [ProducesResponseType(typeof(void), 400)]
        [ProducesResponseType(typeof(void), 500)]
        public async Task<IActionResult> GetByEmail(string email)
        {
            if (String.IsNullOrEmpty(email))
            {
                return StatusCode(400, "Invalid parameter(s).");
            }

            // Check if email address is valid
            if (!Regex.Match(email, @"^([\w\.\-]+)@((?!\.|\-)[\w\-]+)((\.(\w){2,3})+)$").Success)
            {
                return StatusCode(400, "This e-mail address is not valid.");
            }

            //Get user
            var data = await _repo.GetUserByEmail(email);
            if (data == null)
            {
                return StatusCode(500, "User could not be found.");
            }

            //Convert to view model
            var result = new UserViewModel();
            result.SetProperties(data, false);

            return Ok(result);
        }

        /// <summary>
        /// Checks if given email and password match a user.
        /// </summary>
        /// <param name="email">Email of user</param>
        /// <param name="password">Password of user</param>
        [HttpGet("authenticate")]
        [ProducesResponseType(typeof(UserViewModel), 200)]
        [ProducesResponseType(typeof(void), 400)]
        [ProducesResponseType(typeof(void), 500)]
        public async Task<IActionResult> Authenticate(string email, string password)
        {
            if (String.IsNullOrEmpty(email) || String.IsNullOrEmpty(password))
            {
                return StatusCode(400, "Invalid parameter(s).");
            }

            //Check if email address is valid
            if (!Regex.Match(email, @"^([\w\.\-]+)@((?!\.|\-)[\w\-]+)((\.(\w){2,3})+)$").Success)
            {
                return StatusCode(400, "This e-mail address is not valid.");
            }

            //Get user
            var data = await _repo.Authenticate(email, password);
            if (data == null)
            {
                return StatusCode(500, "Invalid credentials.");
            }

            //Convert to view model
            var result = new UserViewModel();
            result.SetProperties(data, false);

            return Ok(result);
        }

        /// <summary>
        /// Creates a user.
        /// </summary>
        /// <param name="model">User object</param>
        [HttpPost("create")]
        [ProducesResponseType(typeof(UserViewModel), 200)]
        [ProducesResponseType(typeof(void), 400)]
        [ProducesResponseType(typeof(void), 500)]
        public async Task<IActionResult> Create([FromBody]UserViewModel model)
        {
            if (model == null)
            {
                return StatusCode(400, "Invalid parameter(s).");
            }

            User user = new User
            {
                FirstName = model.FirstName,
                LastName = model.LastName,
                CompanyName = model.CompanyName,
                Email = model.Email,
                Password = model.Password,
                Role = model.Role,
                Picture = model.Picture
            };

            // Check if email address is valid
            if (!Regex.Match(user.Email, @"^([\w\.\-]+)@((?!\.|\-)[\w\-]+)((\.(\w){2,3})+)$").Success)
            {
                return StatusCode(400, "This e-mail address is not valid.");
            }

            //Add default picture when none is uploaded
            if (String.IsNullOrEmpty(user.Picture))
            {
                user.Picture = "data:image/jpg;base64,";
            }

            //If password is empty, then add
            if (String.IsNullOrEmpty(user.Password))
            {
                user.Password = String.Format("{0}{1}{2}", user.LastName, user.FirstName[0], DateTime.Now.Minute);
            }
            var tempPassword = user.Password;

            //Insert user
            var data = await _repo.Insert(user);
            if (data == null)
            {
                return StatusCode(500, "A problem occured while saving the record. Please try again!");
            }

            var result = new UserViewModel();
            result.SetProperties(data, true);

            //Send email with credentials
            var credentialsUser = user;
            credentialsUser.Password = tempPassword;

            await _email.SendCredentials(credentialsUser);

            return Ok(result);
        }

        /// <summary>
        /// Updates a user.
        /// </summary>
        /// <param name="model">User object</param>
        [HttpPut("update")]
        [ProducesResponseType(typeof(UserViewModel), 200)]
        [ProducesResponseType(typeof(void), 400)]
        [ProducesResponseType(typeof(void), 500)]
        public async Task<IActionResult> Update([FromBody]UserViewModel model)
        {
            if (model == null)
            {
                return StatusCode(400, "Invalid parameter(s).");
            }

            User user = new User
            {
                Email = model.Email,
                Password = model.Password,
                FirstName = model.FirstName,
                LastName = model.LastName,
                CompanyName = model.CompanyName,
                Role = model.Role,
                Picture = model.Picture
            };

            //Update user
            var data = await _repo.Update(user);
            if (data == null)
            {
                return StatusCode(500, "A problem occured while updating the record. Please try again!");
            }

            var result = new UserViewModel();
            result.SetProperties(data, false);

            return Ok(result);
        }

        /// <summary>
        /// Deletes a user.
        /// </summary>
        /// <param name="email">Id of user</param>
        [HttpDelete("delete")]
        [ProducesResponseType(typeof(void), 200)]
        [ProducesResponseType(typeof(void), 400)]
        [ProducesResponseType(typeof(void), 500)]
        public async Task<IActionResult> Delete(string email)
        {
            if (String.IsNullOrEmpty(email))
            {
                return StatusCode(400, "Invalid parameter(s).");
            }

            // Check if email address is valid
            if (!Regex.Match(email, @"^([\w\.\-]+)@((?!\.|\-)[\w\-]+)((\.(\w){2,3})+)$").Success)
            {
                return StatusCode(400, "This e-mail address is not valid.");
            }

            //Remove user
            var succeeded = await _repo.Delete(email);
            if (!succeeded)
            {
                return StatusCode(500, "A problem occured while removing the record. Please try again!");
            }

            return Ok("Success");
        }

        /// <summary>
        /// Uploads an image and adds path to user
        /// </summary>
        [HttpPost("upload")]
        [ProducesResponseType(typeof(UserViewModel), 200)]
        [ProducesResponseType(typeof(void), 400)]
        [ProducesResponseType(typeof(void), 500)]
        public async Task<IActionResult> Upload()
        {
            StringValues json;
            Request.Form.TryGetValue("model", out json);

            var file = Request.Form.Files.FirstOrDefault();
            var model = JsonConvert.DeserializeObject<UserViewModel>(json);

            if (model == null || file == null)
            {
                return StatusCode(400, "Invalid parameter(s).");
            }

            if (file.Length == 0)
            {
                return StatusCode(400, "File is empty.");
            }

            // Get user
            var userToUpdate = await _repo.GetUserByEmail(model.Email);

            // Add image to user
            var extension = file.FileName.Split(".")[1];
            string imageBinary;

            using (var ms = new MemoryStream())
            {
                file.CopyTo(ms);
                var fileBytes = ms.ToArray();
                imageBinary = Convert.ToBase64String(fileBytes);
            }

            // Update user
            userToUpdate.Picture = "data:image/" + extension + ";base64," + imageBinary;
            await _repo.Update(userToUpdate);

            return Ok(model);
        }

        /// <summary>
        /// Resets a users password and sends an email.
        /// </summary>
		/// <param name="email">Email of user</param>
        [HttpGet("resetPassword")]
        [ProducesResponseType(typeof(void), 200)]
        [ProducesResponseType(typeof(void), 500)]
        public async Task<IActionResult> ResetPassword(string email)
        {
            if (String.IsNullOrEmpty(email))
            {
                return StatusCode(400, "Invalid parameter(s).");
            }

            // Check if email address is valid
            if (!Regex.Match(email, @"^([\w\.\-]+)@((?!\.|\-)[\w\-]+)((\.(\w){2,3})+)$").Success)
            {
                return StatusCode(400, "This e-mail address is not valid.");
            }

            // Reset password
            var data = await _repo.ResetPassword(email);
            if (String.IsNullOrEmpty(data))
            {
                return StatusCode(404, String.Format("Unable to reset your password."));
            }
            else if (!String.IsNullOrEmpty(data))
            {
                await _email.SendPasswordResetEmail(email, data);
            }

            return Ok();
        }
    }
}
